{{ ((options, serverSideFiltering) => ( 
  (function() {
    const cliente = (Select_public_clientes2.selectedOptionLabel || "").toUpperCase();

    // CASO 1: ES WASHTEC
    if (cliente.includes("WASHTEC")) {
       // Por defecto seleccionamos la primera zona (Repsol/Campsa 50€)
       // Pero como es un desplegable, tú podrás cambiarlo a Zona 2, 3 o 4 manualmente.
       return 50; 
    }

    // CASO 2: CUALQUIER OTRO (Repsol cliente directo, Particulares...)
    // Les ponemos la tarifa plana de 65€.
    return 65;
  })()
))(select_zona.options, select_zona.serverSideFiltering) }}

