onClick:
{{ 
    (async function() {
      if (!tabla_albaranes.selectedRow) { 
        showAlert('Selecciona un albarán para borrar', 'warning'); 
        return; 
      }
      try {
        await q_delete_albaran_cascade.run({ id: tabla_albaranes.selectedRow.id_stel });
        showAlert('Albarán eliminado correctamente', 'success');
        closeModal('Modal1');
        await q_albaranes.run(); // Refrescar tabla
      } catch (err) {
        showAlert('Error al eliminar: ' + err.message, 'error');
      }
    })()
  }}
