{{
(async () => {
  // Anti doble-click
  if (appsmith.store.guardando) {
    showAlert("Ya estoy guardando, jefe ðŸ˜…", "warning");
    return;
  }

  await storeValue("guardando", true);

  try {
    const esEdicion = !!appsmith.URL.queryParams.id;

    // Ejecuta el query correcto
    const res = esEdicion
      ? await update_cliente.run()
      : await insert_cliente.run();

    // Salir de modo ediciÃ³n
    await storeValue("editando", false);

    // Aviso OK
    showAlert(esEdicion ? "Cliente actualizado âœ…" : "Cliente creado âœ…", "success");

    // Si es nuevo, navegar a la ficha del nuevo ID
    if (!esEdicion) {
      const newId = res?.[0]?.id;
      if (newId) {
        navigateTo("CLIENTE_FICHA", { id: newId }, "SAME_WINDOW");
      }
    } else {
      // Si editas, refresca datos para que se vean los cambios
      await get_cliente_by_id.run();
    }

  } catch (err) {
    showAlert("Error al guardar: " + (err?.message || err), "error");
  } finally {
    await storeValue("guardando", false);
  }
})()
}}
