menuItem1.onClick:
{{ 
  (async function() {
      if (Table1.selectedRows.length === 0) {
          showAlert('Selecciona algo para borrar', 'warning');
          return;
      }

      try {
          // 1. Mandamos borrar
          await API_EliminarProducto.run({ 
              ids: Table1.selectedRows.map(row => row.id).join(',') 
          });

          showAlert('Borrando...', 'info');

          // 2. ðŸ›‘ EL TRUCO: Esperamos medio segundo (500ms)
          // Esto da tiempo a que la base de datos termine de escribir
          await new Promise(resolve => setTimeout(resolve, 500));

          // 3. Ahora sÃ­, pedimos la lista
          await API_ListarProductos.run();

          // 4. Limpiamos y avisamos
          resetWidget('Table1');
          showAlert('Â¡Eliminado correctamente!', 'success');

      } catch (error) {
          showAlert('Error al eliminar', 'error');
      }
  })()
}}

