onClick:
{{ 
  (async function() {
      try {
          if (appsmith.store.modoFicha === 'editar') {
              await UpdateProducto.run(); 
              showAlert('✅ Actualizado correctamente', 'success');
          } else {
              await API_InsertarProducto.run();
              showAlert('✅ Producto creado', 'success');
          }

          await q_getProductos.run();
          await API_ListarProductos.run();

          resetWidget("Image1"); 
          CheckEditar.setValue(false);
          closeModal('ModalFichaProducto');

      } catch (error) {
          showAlert('❌ Error: ' + error.message, 'error');
          console.log(error);
      }
  })()
}}
