onClick:
{{
(async () => {
  if (appsmith.store.guardando) {
    showAlert("Ya estoy guardando, espera... ðŸ˜…", "warning");
    return;
  }

  await storeValue("guardando", true);

  try {
    const esNuevo = !appsmith.store.proveedorSeleccionado;
    const params = {
      nombre_fiscal: Input_NombreFiscal_Prov.text,
      nombre_comercial: Input_NombreComercial_Prov.text,
      cif_nif: Input_Cif_Prov.text,
      direccion: Input_Direccion_Prov.text,
      poblacion: Input_Poblacion_Prov.text,
      provincia: Input_Provincia_Prov.text,
      codigo_postal: Input_CodigoPostal_Prov.text,
      telefono: Input_Telefono_Prov.text,
      email: Input_Email_Prov.text,
      referencia: Input_Referencia_Prov.text
    };

    if (esNuevo) {
      const result = await q_insert_proveedor.run(params);
      const nuevoId = result[0]?.id;
      await storeValue("proveedorSeleccionado", { id: nuevoId, ...params });
      showAlert("Proveedor creado âœ…", "success");
    } else {
      params.id = appsmith.store.proveedorSeleccionado.id;
      await q_upsert_proveedor.run(params);
      await storeValue("proveedorSeleccionado", {
        ...appsmith.store.proveedorSeleccionado,
        ...params
      });
      showAlert("Proveedor actualizado âœ…", "success");
    }

    await storeValue("editando", false);

  } catch (err) {
    showAlert("Error al guardar: " + (err?.message || err), "error");
  } finally {
    await storeValue("guardando", false);
  }
})()
}}
